import React from 'react'
import styled from 'styled-components'
import tw from 'twin.macro'
import { SCREENS } from "../../components/responsive";

import JamisBikeImg from '../../../assets/images/jamis-big.png'
import BlobImg from '../../../assets/images/blob.svg'


const TopSectionContainer = styled.div`
min-height: 400px;
margin-top: 6em;
${tw`
    w-full
    max-w-screen-2xl
    flex
    justify-between
    pl-3
    pr-3
    lg:pl-12
    lg:pr-12
`};
`;

const LeftContainer = styled.div`
${tw`
    w-1/2
    flex
    flex-col 
`};
`;

const RightContainer = styled.div`
${tw`
    w-1/2
    flex
    flex-col 
    relative
    mt-20
`};
`;

const Slogan = styled.h1`
${tw`
    font-bold
    text-2xl
    xl:text-6xl
    sm:text-3xl
    md:text-5xl
    lg:font-black
    md:font-extrabold
    text-black
    mb-4
    sm:leading-snug 
    lg:leading-normal 
    xl:leading-relaxed
    pl-4
`};
`;

const Description = styled.p`
${tw`
    text-xs
    lg:text-sm
    xl:text-lg
    sm:max-h-full
    overflow-hidden
    max-h-12 
    text-gray-800
    pl-4
`};
`;

const BlobContainer = styled.p`
    width: 25em;
    height: 15em;
    position: absolute;
    right: -5em;
    top: -9em;
    z-index: -1;
    transform: rotate(-8deg);

    img {
        width: 100%;
        height: auto;
        max-height: max-content;
    }

    @media (min-width: ${SCREENS.sm}) {
        width: 40em;
        max-height: 10em;
        right: -9em;
        top: -16em;
        transform: rotate(-6deg);
    }

    @media (min-width: ${SCREENS.lg}) {
        width: 50em;
        max-height: 20em;
        right: -9em;
        top: -16em;
        transform: rotate(-6deg);
    }

    @media (min-width: ${SCREENS.xl}) {
        width: 70em;
        max-height: 30em;
        right: -14em;
        top: -20em;
        transform: rotate(-6deg);
    }
`;


const StanaloneBike = styled.p`
    width: auto;
    height: 10.4em;
    right: 4em;
    top: 4.4em;
    position: absolute;
    img {
        width: auto;
        height: 100%;
        max-width: fit-content;
    }
    @media (min-width: ${SCREENS.sm}) {
        height: 16em;
        right: 4em;
        top: 4.4em;
        position: absolute;
    }

    @media (min-width: ${SCREENS.lg}) {
        height: 21em;
        right: 8em;
        top: 6em; 
    }

    @media (min-width: ${SCREENS.xl}) {
        height: 10.4em;
        right: 4em;
        top: 4.4em; 
    }
`;


export function TopSection() {

    return <TopSectionContainer>
        <LeftContainer>
            <Slogan>
                Rent The Best Quality Cycles With Us
            </Slogan>
            <Description>
                Always choose the best bike from our local stores or order it remote
                at the best price for you and get the best quality cycles for as long you like
            </Description>
        </LeftContainer>
        <RightContainer>
            <BlobContainer>
                <img src={BlobImg} alt="Blob Img" />
                <StanaloneBike>
                <img src={JamisBikeImg} alt="Jamis" />
                </StanaloneBike>
            </BlobContainer>
        </RightContainer>
    </TopSectionContainer>

}
